mapper.select:
SELECT a.id as qusetion_id ,a.text ,a.title_category_id ,CASE WHEN(a.title_category_id =1)THEN'배송' WHEN(a.title_category_id =2)THEN'사이즈' WHEN(a.title_category_id =3)THEN'상품상세문의' END AS title_category , replace (substring_index ( a.create_date, ' ',1),'-','.') as create_date ,b.name ,c.description FROM product_question a ,user b ,product c WHERE a.product_id=c.id and a.user_id = b.id
and a.question_answer=#{questionAnswer}
and a.id=#{qustionId}
