mapper.select:
select count(*) as count FROM( select a.name ,b.description ,b.content ,d.save_file_name ,format(price,0) as price from category a, product b, product_image c, file_info d, product_price e where a.id=b.category_id and b.id=c.product_id and c.file_id=d.id and e.product_id=b.id
and a.id=#{id}
) A;

SELECT * FROM ( SELECT @rownum := @rownum + 1 AS RN, A.* FROM ( select a.name ,b.description ,b.content ,d.save_file_name ,format(price,0) as price from category a, product b, product_image c, file_info d, product_price e where a.id=b.category_id and b.id=c.product_id and c.file_id=d.id and e.product_id=b.id
and a.id=#{id}
ORDER BY b.id DESC ) A,(SELECT @rownum :=0) AS r ) B WHERE RN BETWEEN #{start} AND #{end}
