mapper.update:
update reservation_info a
	left outer join reservation_info_price b on a.id=b.reservation_info_id
	set a.modify_date = #{modifyDate},
	b.count=#{count}
where a.id=#{id}

mapper.select:
SELECT b.description, a.cancle_flag, a.id as reservation_info_id, c.price,c.discount_rate, d.count, a.create_date, a.reservation_date FROM reservation_info a, product b, product_price c, reservation_info_price d WHERE 1=1 and a.product_id=b.id and b.id=c.product_id and a.id=d.reservation_info_id and a.user_id=#{userId}
and a.reservation_date between #{startDate} and #{lastDate}
and a.cancle_flag between 1 and 5 order by a.cancle_flag;
